import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-currency-converter',
  templateUrl: './currency-converter.component.html',
  styleUrls: ['./currency-converter.component.css'],
})
export class CurrencyConverterComponent implements OnInit {
  amount: number = 0;
  fromCurrency: string = '';
  toCurrency: string = '';
  convertedAmount: number = 0;
  rates: any = {};

  ngOnInit(): void {
    this.getCurrencies();
  }

  async getCurrencies(): Promise<void> {
    const response = await fetch(
      'https://bank.gov.ua/NBUStatService/v1/statdirectory/exchange?json'
    );
    const data = await response.json();

    data.forEach((currency: any) => {
      this.rates[currency.cc] = currency.rate;
    });

    this.rates.UAH = 1.0;
  }

  convertValue(): void {
    const inputValue = parseFloat(this.amount.toString());

    let convertedValue;

    if (this.fromCurrency === this.toCurrency) {
      convertedValue = inputValue;
    } else if (this.fromCurrency === 'UAH') {
      convertedValue = inputValue / this.rates[this.toCurrency];
    } else if (this.toCurrency === 'UAH') {
      convertedValue = inputValue * this.rates[this.fromCurrency];
    } else {
      convertedValue =
        (inputValue * this.rates[this.fromCurrency]) /
        this.rates[this.toCurrency];
    }

    this.convertedAmount = parseFloat(convertedValue.toFixed(2));
  }

  convertBack(): void {
    const inputValue = parseFloat(this.convertedAmount.toString());

    let convertedValue;

    if (this.fromCurrency === this.toCurrency) {
      convertedValue = inputValue;
    } else if (this.fromCurrency === 'UAH') {
      convertedValue = inputValue / this.rates[this.toCurrency];
    } else if (this.toCurrency === 'UAH') {
      convertedValue = inputValue * this.rates[this.fromCurrency];
    } else {
      convertedValue =
        (inputValue * this.rates[this.toCurrency]) /
        this.rates[this.fromCurrency];
    }

    this.amount = parseFloat(convertedValue.toFixed(2));
  }
  
}
<div class="container pt-6 pb-6">
  <div class="row justify-content-center">
    <div class="col-6">
      <div class="card p-3">
        <h1 class="h2 mb-4">Currency Converter</h1>

        <div class="courses">
          <div class="course-item card card-body">
            <div class="course-item-title">USD</div>
            <div class="course-item-value">{{ rates.USD | number: '1.2-2' }}</div>
          </div>
          <div class="course-item card card-body">
            <div class="course-item-title">EUR</div>
            <div class="course-item-value">{{ rates.EUR | number: '1.2-2' }}</div>
          </div>
          <div class="course-item card card-body">
            <div class="course-item-title">GBP</div>
            <div class="course-item-value">{{ rates.GBP | number: '1.2-2' }}</div>
          </div>
        </div>

        <div class="row mb-1">
          <div class="col">
            <label for="input">I Give:</label>
            <select class="form-control" [(ngModel)]="fromCurrency">
              <option value="UAH">UAH</option>
              <option value="USD">USD</option>
              <option value="EUR">EUR</option>
              <option value="GBP">GBP</option>
            </select>
          </div>
          <div class="col">
            <label for="result">I Take:</label>
            <select  class="form-control" [(ngModel)]="toCurrency">
              <option value="USD">USD</option>
              <option value="EUR">EUR</option>
              <option value="GBP">GBP</option>
              <option value="UAH">UAH</option>
            </select>
          </div>
        </div>

        <div class="row">
          <div class="col">
            <input type="number" class="form-control" [(ngModel)]="amount" (input)="convertValue()" autofocus />
          </div>
          <div class="col">
            <input type="number" class="form-control" [(ngModel)]="convertedAmount" (input)="convertBack()" />
          </div>
        </div>
      </div>
    </div>
  </div>
</div>
