import { NgModule } from '@angular/core';
import { BrowserModule } from '@angular/platform-browser';
import { FormsModule } from '@angular/forms';

import { AppRoutingModule } from './app-routing.module';

import { CurrencyConverterComponent } from './currency-converter/currency-converter.component'; // Імпортуйте CurrencyConverterComponent

@NgModule({
  declarations: [
    AppComponent,
    CurrencyConverterComponent, // Додайте CurrencyConverterComponent до declarations
  ],
  imports: [
    BrowserModule,
    AppRoutingModule,
    FormsModule, // Додайте FormsModule, якщо використовуєте його
  ],
  providers: [],
  bootstrap: [AppComponent],
})
export class AppModule {}
